import React , {Fragment,useState}from 'react';
import {

  Link
} from "react-router-dom";

const Register=()=> {
  const [formData , setformData]=useState({
    name:"",
    email:"",
    password:"",
    password2:""
  });
  const{name,email,password,password2}=formData;
  const onChange=e=>{
  setformData({...formData,[e.target.value]:e.target.value})
  }
  const onSubmit=e=>{
    e.preventDefault();
    if(password  !== password2){
   console.log("mauvais password try again ")
    }else{
      console.log('great you entered the right password !',formData)
    }
  }
  return (
    <Fragment>

      <h1 className="large text-primary">Sign Up</h1>
      <p className="lead"><i className="fas fa-user"></i> Create Your Account</p>
      <form className="form" action="create-profile.html" onSubmit={e=>onSubmit(e)} >
        <div className="form-group">
          <input type="text" placeholder="Name"  value="name" onChange={e=>onChange(e)} required />
        </div>
        <div className="form-group">
          <input type="email" placeholder="Email Address"  value ="email" onChange={e=>onChange(e)}/>
          <small className="form-text"
            >This site uses Gravatar so if you want a profile image, use a
            Gravatar email</small
          >
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"

            minLength="6"
            value="password"
            onChange={e=>onChange(e)}
          />
        </div>
        <div className="form-group">
          <input
             type="password2"
            placeholder="Confirm Password"

            minLength="6"
            value="password2"
           onChange={e=>onChange(e)}
          />
        </div>
        <input type="submit" className="btn btn-primary" value="Register" />
      </form>
      <p className="my-1">
        Already have an account? <Link to="/loging">Sign In</Link>
      </p>

  </Fragment>
  );
}
export default Register;
