import React, {  useState } from 'react';
// import axios from 'axios'
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import { Field, reduxForm } from 'redux-form'
import TextField from '@material-ui/core/TextField'
import submit from './submit'
import { Divider } from '@material-ui/core';
import { Button } from '@material-ui/core';
import FileUpload from './FileUpload';
import CloseIcon from '@material-ui/icons/Clear';
import Icon from '@material-ui/core/Icon';
import { useHistory } from "react-router-dom";

import userAvatar from '../icons/userAvatar.jpg';

const styles = makeStyles((theme) => ({
    avatar: {
      backgroundColor: theme.palette.secondary.main,
      alignSelf: 'center',
      margin: 'auto',
      marginBottom: '0px',
      width:'90px',
      height:'90px',
    },
    icon: {
      width:'90px',
      height:'90px',
    },
    photo: {
      display: 'flex',
    },
    title: {
        fontFamily: 'Roboto',
        fontStyle: 'normal',
        fontWeight: '500',
        fontSize: '16px',
        lineHeight: '19px',
        textAlign: 'center',
        color: theme.palette.primary.main,
        margin: 'auto',
    },
    input: {
      paddingLeft: '8px'
    },
  }));

  const renderTextField = ({
    label,
    input,
    meta: { touched, invalid, error },
    ...custom
  }) => {
    const classes = styles();
    return(
    <TextField
      label={label}
      placeholder={label}
      error={touched && invalid}
      helperText={touched && error}
      style={{width: '100%'}}
      inputProps={{className:classes.input}}
      InputLabelProps={{className:classes.input}}
      {...input}
      {...custom}
    />
    )
    }
  const validate = values => {
    const errors = {}
    const requiredFields = [
      'lastName',
      'headline',
      'currentPosition',
      'country',
      'city',
      'province',
      'summary',  ]
    requiredFields.forEach(field => {
      if (!values[field]) {
        errors[field] = 'Required'
      }
    })
    return errors
  }

function Profile(props) {
  const { handleSubmit, pristine, reset, submitting, userId, currentUser, imgSrc } = props;
  const classes = styles();
  const [initialValues, setInitialValues] = useState(null);
  const [isDataFetched, setIsDataFetched] = useState(false);
//   useEffect(() => {
//     const fetchData= async () => {
//       await axios.put(`${process.env.REACT_APP_URL}profile/${userId}`)
//       .then(res => {
//         setInitialValues({
//           firstName: currentUser.firstName,
//           lastName: currentUser.lastName,
//           ...res.data})
//         setIsDataFetched(true)
//       });
//     }
//     if(!isDataFetched) {
//       fetchData()
//     }
//   } ,
//   []
//   );
  const history = useHistory();
  function handleClick() {
    history.push("/offers");
  }
  return (
    <form initialValues={initialValues} style={{width: '87%', margin: 'auto'}} pristine onSubmit={handleSubmit(submit)}>
        <Grid item  container>
            <Grid item xs={3} sm={3}>
                <div onClick={handleClick} className={classes.title} style={{alignSelf: 'left', marginTop: '-3px'}}>
                  <Icon component={CloseIcon} />
                </div>
            </Grid>
            <Grid item xs={6} sm={6}>
                <div className={classes.title} style={{alignSelf: 'center'}}>
                    Profile Settings
                </div>
            </Grid>
            <Grid item xs={3} sm={3}>
                <Button type="submit" className={classes.title}  style={{alignSelf: 'right', paddingTop:'0px', textTransform: 'none'}} >
                    Save
                </Button>
            </Grid>
        </Grid>

        <Grid item style={{marginTop: '30px'}} container>
        <Grid item xs={4} sm={4}>
        </Grid>
        <Grid item xs={4} sm={4}>
            <Avatar className={classes.avatar}>
                <img src={imgSrc ? imgSrc : userAvatar} width="90" height="90" className={classes.icon}/>
            </Avatar>
            <Field
                name="photo"
                component={FileUpload}
                label="last Name"
                userId={userId}
            />
            </Grid>
        </Grid>
      <div style={{display: 'flex'}}>
            <Field
                name="firstName"
                component={renderTextField}
                label="First Name"
            />
        <Divider orientation="vertical" flexItem/>
            <Field
                name="lastName"
                component={renderTextField}
                label="last Name"
            />
      </div>
      <div>
        <Field name="headline" component={renderTextField} label="Headline" />
      </div>
      <div>
        <Field name="currentPosition" component={renderTextField} label="Current Position" />
      </div>
      <div>
        <Field name="country" component={renderTextField} label="Country" />
      </div>

      <div style={{display: 'flex'}}>
            <Field
                name="city"
                component={renderTextField}
                label="City/District"
            />
        <Divider orientation="vertical" flexItem/>
            <Field
                name="province"
                component={renderTextField}
                label="Province/state"
            />
      </div>
      <div>
        <Field name="summary" component={renderTextField} multiline rows={10} label="Summary" />
      </div>
    </form>
  );
}

export default reduxForm({
  form: 'Profile', // a unique identifier for this form
  validate,
})(Profile)
